package enity.proxy;import java.lang.reflect.InvocationHandler;import java.lang.reflect.Method;public class ProxyHander implements InvocationHandler {    Object object;    public ProxyHander(Object object) {        this.object = object;    }    @Override    public Object invoke(Object proxy, Method method, Object[] args) throws Throwable {        System.out.println("某个方法执行之前的操作！");        Object invoke = method.invoke(object, args);        System.out.println("某个方法执行之后的操作！"+object.getClass().getName());        return invoke;    }}